mapper.select:
SELECT acc_num
FROM account
where account_id = #{accountId}

mapper.insert:
INSERT INTO ttf.ttf(
		user_id, account_id, start_date, end_date, acc_ck, account_num, cans
	) VALUES ( 
		#{user_id}, #{account_id}, #{start_date}, #{end_date}, #{acc_ck}, #{account_num}, 
		#{cans}
	)

mapper.select:
SELECT limitAmount
FROM ttf.ttf
WHERE user_id=#{userPK}

mapper.update:
